SELECT DISTINCTROW ProgramacionExamenes.TipoExamen, ProgramacionExamenes.FechaExamen, ProgramacionExamenes.ClaveExamen, ProgramacionExamenes.HoraExamen, ProgramacionExamenes.SalonExamen, ProgramacionExamenes.MaestroBase, ProgramacionExamenes.Examinador1, ProgramacionExamenes.Examinador2, RelacionExamenes.Horario, RelacionExamenes.Credencial, RelacionExamenes.Nombre, RelacionExamenes.GradoNivel, RelacionExamenes.ReciboPago, RelacionExamenes.FechaPago, RelacionExamenes.cantidad, RelacionExamenes.neto, RelacionExamenes.SePresento, RelacionExamenes.Aprobo, RelacionExamenes.[Certificado Num]
FROM ProgramacionExamenes INNER JOIN RelacionExamenes ON ProgramacionExamenes.ClaveExamen=RelacionExamenes.ClaveExamen
WHERE (((ProgramacionExamenes.TipoExamen)=[Tipo de Examen]) AND ((ProgramacionExamenes.FechaExamen) Between [Fecha Inicial] And [Fecha Final]))
ORDER BY ProgramacionExamenes.TipoExamen, ProgramacionExamenes.FechaExamen, ProgramacionExamenes.HoraExamen, RelacionExamenes.Horario, RelacionExamenes.Credencial;
